import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

// exercise component --> implemented as a functional react component
// functional react component: accept props and return jsx
// accepts the props that were passed into it, returns a table row
const Exercise = props => (
    <tr>
      <td>{props.exercise.username}</td>
      <td>{props.exercise.description}</td>
      <td>{props.exercise.duration}</td>
      <td>{props.exercise.date.substring(0,10)}</td>
      <td>
        <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
      </td>
    </tr>
  )

// exerciseList component - class component
export default class ExercisesList extends Component {
    constructor(props) {
      super(props);

      this.deleteExercise = this.deleteExercise.bind(this)

      this.state = {exercises: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/exercises/')
        .then(response => {
            this.setState({ exercises: response.data })
        })
        .catch((error) => {
            console.log(error);
        })
    }

    deleteExercise(id) {
        axios.delete('http://localhost:5000/exercises' + id)
        .then(res => console.log(res.data));

        // react automatically updates the page with new exercises
        // for every element in the exercises array, we will filter it, we will 
        // return it if el_id != id
        this.setState({
            exercises: this.state.exercises.filter(el => el._id != id)
        })
    }

    // for every element called current exercise, it returns a component
    // the component is a row for the table, we pass in 3 props
    exerciseList() {
        return this.state.exercises.map(currentexercise => {
          return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
        })
      }

    render(){
        return (
            <div>
            <h3>Logged Exercises</h3>
            <table className="table">
              <thead className="thead-light"> 
                <tr>
                  <th>Username</th>
                  <th>Description</th>
                  <th>Duration</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                { this.exerciseList() }
              </tbody>
            </table>
          </div>
        )
    }
}
